import json
import threading

# Funktion zum Schreiben der Daten in die JSON-Datei


def write_to_json(data):
    with open('data.json', 'w') as file:
        json.dump(data, file)

# Funktion zum Lesen der Daten aus der JSON-Datei


def read_from_json(fname):
    with open(str(fname+'.json'), 'r') as file:
        data = json.load(file)
        return data


# Beispiel-Daten zum Schreiben
data_to_write = {'name': 'John', 'age': 30}

# Schreibender Thread


def writer_thread():
    # Schreibvorgang sperren
    lock.acquire()
    try:
        # Daten schreiben
        write_to_json(data_to_write)
    finally:
        # Schreibvorgang freigeben
        lock.release()

# Lesender Thread


def reader_thread():
    # Lesenvorgang sperren
    lock.acquire()
    try:
        # Daten lesen
        data = read_from_json()
        print('Gelesene Daten:', data)
    finally:
        # Lesenvorgang freigeben
        lock.release()


# Sperre für den Zugriff auf die JSON-Datei
lock = threading.Lock()

# Threads erstellen
writer = threading.Thread(target=writer_thread)
reader = threading.Thread(target=reader_thread)

# Threads starten
writer.start()
reader.start()

# Warten, bis beide Threads beendet sind
writer.join()
reader.join()


